    <script src="https://code.jquery.com/jquery-1.11.1.min.js"></script>
    <script src="lib/ajaxtim-1.0.min.js"></script>
    <script src="lib/babyPushPoster.min.js"></script>
    <!-- You can remove basepack if you want, it will just enhance the error messages
    coming from ajaxtim (dialogg instead of js native alert) -->
    <script src="lib/uii.basepack.min.js"></script>
    <link rel="stylesheet" href="lib/uii.basepack.min.css">


    <link rel="stylesheet" href="lib/style.css">


    <title>Shell Command Supervisor</title>


    <style>

        header span {
            font-size: 16px;
            font-style: italic;
        }

        .loader {
            display: none;
            height: 40px;
            margin-top: 10px;
        }

        .loader.loading {
            display: block;
        }

        .loader img {
            vertical-align: middle;
        }
    </style>
</head>

<body>

<header>
    <h1>Shell Command Supervisor</h1>
    <span>shell commands bookmarks</span>

    <p>by LingTalfi 2014-10-23, last update: 2014-11-17</p>
</header>

<section class="maininterface">


    <div id="loader" class="loader"><img
            src="data:image/gif;base64,"/>
        Task executing, please wait...
    </div>
    <div id="cmd-output" class="cmd-output">

    </div>
    <div class="rightmenucontainer">
        <nav id="rightmenu" class="rightmenu">
            $rightmenu$
        </nav>

    </div>

    <div style="clear:both">
        <a href="#" id="totop">Go to top</a>
    </div>
</section>

<script>


    (function ($) {
        $(document).ready(function () {


            var serviceUrl = '$serviceUrl$';
            var oPoster = new babyPushPoster();
            var jLoader = $('#loader');
            var jOutput = $("#cmd-output");
            var jRightMenu = $("#rightmenu");
            var jToTop = $("#totop");


            //------------------------------------------------------------------------------/
            // USER SPACE
            //------------------------------------------------------------------------------/
            function removeLoader() {
                jLoader.removeClass("loading");

            }

            function addLoader() {
                jLoader.addClass("loading");
            }

            function getCatName(jInner) {
                return jInner.closest('ul').attr("data-domain");
            }

            function getCmdName(jInner) {
                return jInner.closest('li').attr("data-name");
            }

            function getItemByInner(jInner) {
                return jInner.closest('li');
            }


            //------------------------------------------------------------------------------/
            // SCRIPT
            //------------------------------------------------------------------------------/
            jRightMenu
                .on('click', function (e) {

                    var jTarget = $(e.target);
                    if (jTarget.hasClass("cmd-trigger")) {

                        jTarget = getItemByInner(jTarget);
                        //------------------------------------------------------------------------------/
                        // BABY PUSH SERVER SCRIPT
                        //------------------------------------------------------------------------------/
                        function executeScript($jTarget) {

                            var taskParams = {
                                category: getCatName($jTarget),
                                name: getCmdName($jTarget)
                            };

                            addLoader();

                            oPoster.sendMessage(serviceUrl, taskParams, function (data) {
                                jOutput.html(data);
                                $("html, body").animate({scrollTop: $(document).height()}, "slow");
                            }, {
                                onEnd: removeLoader
                            }, {
                                onErrorBefore: removeLoader
                            });
                        }


                        if (jTarget.hasClass("confirm")) {
                            var zeCmdname = getCmdName(jTarget);
                            if (window.confirm("Do you really want to execute the " + zeCmdname + " cmd")) {
                                executeScript(jTarget);
                            }
                        }
                        else {
                            executeScript(jTarget);
                        }
                    }
                    else if (jTarget.hasClass('description-toggle')) {
                        var jDescription = jTarget.closest('li').find('.description:first');
                        if (jDescription.hasClass('hidden')) {
                            jDescription.removeClass("hidden");
                            jTarget.html("&#x229f;");
                        }
                        else {
                            jDescription.addClass("hidden");
                            jTarget.html("&#x229e;");
                        }
                    }
                    else if (jTarget.hasClass('cat-toggle')) {
                        var jUl = jTarget.closest('ul');
                        if (jUl.hasClass('hidecats')) {
                            jUl.removeClass("hidecats");
                            jTarget.html("&#x229f;");
                        }
                        else {
                            jUl.addClass("hidecats");
                            jTarget.html("&#x229e;");
                        }
                    }
                    return false;
                });


            jToTop.on("click", function () {
                $("html, body").animate({scrollTop: 0}, "slow");
                return false;
            });


        });
    })(jQuery);
</script>


</body>
</html>